mapper.select:
select 
	ROW_NUMBER() OVER () AS rnum,*
from(
	SELECT 
	   mtncd,frtrlnm, lat ,lot, aslaltide  ,SPLIT_PART(REPLACE(addrnm, ',', ''), ' ', 1) AS addrnm
	FROM 
	    mountain_info_100
)
where 
	addrnm = #{addrnm}

mapper.update:
UPDATE r_board
SET deadline_status='Y'
WHERE board_id=#{boardId}
